import { ref, watch, computed, onMounted } from 'vue';
import { Chart, LineElement, PointElement, LineController, LinearScale, CategoryScale, Tooltip } from 'chart.js';
export default {
  name: 'LineChart',
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  setup: function setup(props) {
    var root = ref(null);
    var chart;
    Chart.register(LineElement, PointElement, LineController, LinearScale, CategoryScale, Tooltip);
    onMounted(function () {
      chart = new Chart(root.value, {
        type: 'line',
        data: props.data,
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              display: false
            },
            x: {
              display: true
            }
          },
          plugins: {
            legend: {
              display: false
            }
          }
        }
      });
    });
    var chartData = computed(function () {
      return props.data;
    });
    watch(chartData, function (data) {
      if (chart) {
        chart.data = data;
        chart.update();
      }
    });
    return {
      root: root
    };
  }
};
